import React, { useState } from "react";
import ReactAnime from 'react-animejs'
import Header from '../components/Header';
import axios from 'axios'
import '../css/Signup.css';

const { Anime } = ReactAnime;

function Signup() {
  const [selectedImage, setSelectedImage] = useState(null);

  // controller state
  const [control, setControl] = useState(null);

  // meta state
  const [meta, setMeta] = useState({
      control: control,
      progress: 100,
      currentTime: 0,
      duration: 0
  });

  // timeline
  var timeline = [];
  timeline.push(
    {
        targets: "#SignupFormArea",
        delay: 10,
        duration: 3000,
        opacity: 100,
        easing: 'easeInOutSine',
    })

  return (
    <React.Fragment>
      <Header />
      <div class="SignupFormArea" id="SignupFormArea">
        <div>
          <div class="SignupTitleArea">
            <div class="SignupTitle">
              Enter Gala.
            </div>
          </div>
          <div class="SignupInputArea">
            <div class="SignupInputTitle">
              First Name
            </div>
            <input type="text" class="SignupInput" required placeholder="First Name" />
          </div>
          <div class="SignupInputArea">
            <div class="SignupInputTitle">
              Last Name
            </div>
            <input type="text" class="SignupInput" required placeholder="Last Name" />
          </div>
          <div class="SignupInputArea">
            <div class="SignupInputTitle">
              Email Address
            </div>
            <input type="text" class="SignupInput" required placeholder="email@email.com" />
          </div>
          <div class="SignupInputArea">
            <div class="SignupInputTitle">
              Password
            </div>
            <input type="text" class="SignupInput" required placeholder="Password" />
          </div>
          <div class="SignupInputArea">
            <div class="SignupInputTitle">
              Profile Picture
            </div>
            <input type="file" class="SignupInput" required onChange={(event) => {
              setSelectedImage(event.target.files[0]);
            }} />
            {selectedImage && (
              <img className="SignupProfilePicture"
                alt="No Image Found"
                src={URL.createObjectURL(selectedImage)}
              />
            )}
          </div>
          <div class="SignupSubmitButton">
            Sign Up
          </div>
        </div>
      </div>
      <Anime initial={timeline}
              control={control}
              setMeta={setMeta}
              animeConfig={{
                  autoplay: true,
                  duration: 4000,
                  easing: "easeInOutSine"
              }}>
        </Anime>
    </React.Fragment>
  );
}

function onSubmit(event){
  event.preventDefault()
  const registered = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      email: this.state.email,
      password: this.state.password
  }

  axios.post('http://localhost:4000/app/signup', registered)
      .then(response => console.log(response.data))
  //window.location = '/home'
  this.setState({
      firstName:'',
      lastName:'',
      email:'',
      password:''
  })
}

export default Signup;
